import React, { Component, useState, useEffect }  from 'react';
import ReactDOM from 'react-dom';
import ReactPaginate from 'react-paginate';

class Home extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      offset: 1
    };
  }

  handleClick = event => {
    event.currentTarget.classList.toggle('active');
  }


  handleMovies() {
    fetch("https://pokeapi.co/api/v2/pokemon?limit=20&offset="+this.state.offset)
      .then(results => {return results.json();})
      .then(data => {
        console.log(data);
        let pokemons = data.results.map(pokemon => {
          return <div key={pokemon.name}>{pokemon.name}</div>;
        });

        this.setState(() => {
          return {
            pokemons: pokemons
          };
        });
      });
  }
  render() {
    return (
      <div>
        <h1>Episodes</h1>{
        this.state.pokemons.map((pokemon, i) => {
          return (
            <div className="pokemon" onClick={this.handleClick} key={i}>{pokemon.name}
              <div className="opening">{pokemon.name}</div>
            </div>
          );
        })
      }
      </div>
    );
  }
}

export default Home;

function nextPage() {
  console.log('asd');
  // offset += 20;
}
