import React, { Component, useState, useEffect }  from 'react';
import ReactDOM from 'react-dom';
import ReactPaginate from 'react-paginate';

class Home extends React.Component {
  constructor(props) {
    super(props);
    this.handlePokemons = this.handlePokemons.bind(this);
    this.state = {
      movies: []
    };
    this.handlePokemons();

    this.state = {
      offset: 1
    };
  }

  handleClick(name) {
    fetch("https://pokeapi.co/api/v2/pokemon/"+name)
      .then(results => {return results.json();})
      .then(data => {
        console.log(data)
        let pokemons = data.abilities.map(pokemon => {
          return <div className="col-md-4 col-12 p-1"><div className="btn btn-primary pokemon w-100" key={pokemon.ability.name}>{pokemon.ability.name}</div></div>;
        });

        this.setState(() => {
          return {
            pokemons: pokemons,
            title: name
          };
        });
      });
  }

  handlePokemons() {
    fetch("https://pokeapi.co/api/v2/pokemon?limit=20&offset="+this.state.offset)
      .then(results => {return results.json();})
      .then(data => {
        let pokemons = data.results.map(pokemon => {
          fetch("https://pokeapi.co/api/v2/pokemon/"+pokemon.name)
            .then(results => {return results.json();})
            .then(data2 => {
            console.log(data2);
              return (
                <div className="col-md-4 col-12 p-1">
                  <div className="btn btn-primary pokemon w-100 d-flex justify-content-start align-items-center" key={pokemon.name} onClick={() => this.handleClick(pokemon.name)}>
                    <img src="https://raw.githubusercontent.com/PokeAPI/sprites/master/sprites/pokemon/1.png" alt="new"/>
                    <span className="me-4">{pokemon.name}</span>
                  </div>
                </div>);
          });
        });

        this.setState(() => {
          return {
            pokemons: pokemons,
            title: "Pokemon List"
          };
        });
      });
  }

  render() {
    return (
      <div>
        <h5 className="fw-bold">{this.state.title}</h5>
        <div className="containter">
          <div className="row">
            {/*{pokemons.map(pokemon => (
              <div key={pokemon.name} className="">
                <div className="btn btn-primary w-100">{pokemon.name}</div>
              </div>
            ))}*/ 
              this.state.pokemons
            }
          </div>
        </div>
        <button className="btn btn-primary">Next</button>
      </div>
    );
  }
}

export default Home;

function nextPage() {
