import React, { Component, useState, useEffect }  from 'react';
import ReactDOM from 'react-dom';
import ReactPaginate from 'react-paginate';

class Home extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      offset: 1
    };
  }

  handleClick = event => {
    event.currentTarget.classList.toggle('active');
  }


  handleMovies() {
    fetch("https://pokeapi.co/api/v2/pokemon?limit=20&offset="+this.state.offset)
      .then(results => {return results.json();})
      .then(data => {
        console.log(data);
        let pokemons = data.results.map(pokemon => {
          return <div key={pokemon.name}>{pokemon.name}</div>;
        });

        this.setState(() => {
          return {
            pokemons: pokemons
          };
        });
      });
  }
  render() {
    return (
      <div>
        <h5 className="fw-bold">Pokemon List</h5>
        <div className="containter">
          <div className="row">

          {
            this.state.pokemons.map((movie, i) => {
              return (
                <div className="pokemon col-md-4 col-12 p-1" onClick={this.handleClick} key={i}>{pokemon.name}
                  <div className="opening">{pokemon.name}</div>
                </div>
              );
            })
          }

            {/*{pokemons.map(pokemon => (
              <div key={pokemon.name} className="">
                <div className="btn btn-primary w-100">{pokemon.name}</div>
              </div>
            ))}*/}
          </div>
        </div>
        <button className="btn btn-primary">Next</button>
      </div>
    );
  }
}

export default Home;

