import React, { Component, useState, useEffect }  from 'react';
import ReactDOM from 'react-dom';
import ReactPaginate from 'react-paginate';

const offset = 1;

class Home extends React.Component {
  handleClick = event => {
    event.currentTarget.classList.toggle('active');
  }

  handleMovies() {
    fetch("https://pokeapi.co/api/v2/pokemon?limit=20&offset="+offset)
      .then(results => {return results.json();})
      .then(data => {
        console.log(data);
        let pokemons = data.results.map(pokemon => {
          return (
            <div>
              <h5 className="fw-bold">Pokemon List</h5>
              <div className="containter">
                <div className="row">

                {
                  this.state.movies.map((movie, i) => {
                    return (
                      <div className="movie" onClick={this.handleClick} key={i}>{movie.title}
                        <div className="opening">{movie.opening_crawl}</div>
                      </div>
                    );
                  })
                }

                  {pokemons.map(pokemon => (
                    <div key={pokemon.name} className="col-md-4 col-12 p-1">
                      <div className="btn btn-primary w-100">{pokemon.name}</div>
                    </div>
                  ))}
                </div>
              </div>
              <button className="btn btn-primary" onClick={() => nextPage()}>Next</button>
            </div>
          );
        });

        this.setState(() => {
          return {
            pokemons: pokemons
          };
        });
      });
  }
}
export default Home;

function nextPage() {
  console.log('asd');
  offset += 20;
}
