import React, { useState, useEffect }  from 'react';
import ReactDOM from 'react-dom';
import ReactPaginate from 'react-paginate';

const offset = 1;
const Home = () => {
  const [error, setError] = useState(null);
  const [isLoaded, setIsLoaded] = useState(false);
  const [pokemonList1, setPokemonList1] = useState([]);
  const [pokemonList2, setPokemonList2] = useState([]);
  const [pokemonList3, setPokemonList3] = useState([]);
  const [pokemonList4, setPokemonList4] = useState([]);
  useEffect(() => {
      fetch("https://pokeapi.co/api/v2/pokemon?limit=20&offset="+offset)
          .then(res => res.json())
          .then(
              (data) => {
                console.log(data.results[0]);
                  setIsLoaded(true);
                  setPokemonList1([data.results[0], data.results[1], data.results[2], data.results[3], data.results[4]]);
                  setPokemonList2([data.results[0+5], data.results[1+5], data.results[2+5], data.results[3+5], data.results[4+5]]);
                  setPokemonList3([data.results[0+10], data.results[1+10], data.results[2+10], data.results[3+10], data.results[4+10]]);
                  setPokemonList4([data.results[0+15], data.results[1+15], data.results[2+15], data.results[3+15], data.results[4+15]]);
              },
              (error) => {
                  setIsLoaded(true);
                  setError(error);
              }
          )
    }, []
  )

  if (error) {
      return <div>Error: {error.message}</div>;
  } else if (!isLoaded) {
      return <div>Loading...</div>;
  } else {
      return (
          <div>
            <h1 className="ms-3">Pokemon List</h1>
            <div className="d-flex">
              <ul>
                {pokemonList1.map(pokemon => (
                  <li key={pokemon.name}>
                      {pokemon.name} 
                  </li>
                ))}
              </ul>
              <ul>
                {pokemonList2.map(pokemon => (
                  <li key={pokemon.name}>
                      {pokemon.name} 
                  </li>
                ))}
              </ul>
              <ul>
                {pokemonList3.map(pokemon => (
                  <li key={pokemon.name}>
                      {pokemon.name} 
                  </li>
                ))}
              </ul>
              <ul>
                {pokemonList4.map(pokemon => (
                  <li key={pokemon.name}>
                      {pokemon.name} 
                  </li>
                ))}
              </ul>
            </div>
            <button className="btn btn-primary" onClick={() => nextPage()}>Next</button>
          </div>
      );
  }
}
export default Home;

function nextPage() {
  const offset = 21;
}
